mapper.update:
	update tb_music
	   set title = #{title}
 	 where no = #{no}

	update tb_music
	   set singer = #{singer}
 	 where no = #{no}

	update tb_music
	   set genre = #{genre}
 	 where no = #{no}

	update tb_music
	   set rel_date = #{relDate}
 	 where no = #{no}

	update tb_music
	   set music_path = #{musicPath}
 	 where no = #{no}

mapper.select:
select no, singer, title
  from tb_music
 where no = #{no}
